mapper.select:
select X.*
		from ( 
		    select rownum as rnum, A.*
		        from (
		SELECT
 		 *
		FROM tb_board
) A
	  ) X
 
		 where X.rnum > #{page.startRow} and X.rnum <= #{page.endRow}

SELECT COUNT(*) AS CNT FROM TB_BOARD WHERE DEL_FLG = 'N'
AND ANSWER_FLG = #{qna_answer}
AND TITLE LIKE '%'||#{keyword}||'%'

SELECT X.* FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM TB_BOARD WHERE DEL_FLG = 'N'
AND ANSWER_FLG = #{active_key}
AND TITLE LIKE '%' || #{keyword} || '%'
ORDER BY BOARD_NO ASC ) A ) X
WHERE X.RNUM >= #{startRow} AND X.RNUM <= #{endRow}
