mapper.select:
select X.*
		from ( 
		    select rownum as rnum, A.*
		        from (
		SELECT
 		 *
		FROM tb_board_mng
) A
	  ) X
 
		 where X.rnum >= #{page.startRow} and X.rnum <= #{page.endRow}

SELECT BOARD_MNG_NO, BOARD_MNG_CD, BOARD_NM, REPLY_FLG, ACTIVE_FLG, FILE_ATTACH_FLG, NOTICE_FLG 
FROM TB_BOARD_MNG
WHERE BOARD_MNG_NO = #{BOARD_MNG_NO}
