mapper.select:
		SELECT	X.*
		FROM 
     	 (
        	select rownum as rnum, A.*
       		from (
               		 SELECT tm.grp_cd, tm.grp_nm, td.code1, td.code_txt
               		 FROM 
               		 	TB_CODE_MASTER TM, TB_CODE_DETAIL TD
                	 WHERE 
                	 	TM.GRP_CD = TD.GRP_CD
			
				AND TM.grp_cd = #{grp_cd}
			
               		 ORDER BY 
               		 	TM.GRP_CD, TD.CODE1
             	  ) A
     	 ) X
     	 
		WHERE X.rnum >= #{page.startRow} and X.rnum < #{page.endRow}
