mapper.update:
INSERT INTO TB_DEPT (
	dept_cd
	,dept_nm
	,dept_num1
	,dept_num2
	,dept_num3
	,dept_fnum1
	,dept_fnum2
	,dept_fnum3
	,active_flg
	,created_by
)
VALUES (
	(SELECT /*+INDEX_DESC(TB_DEPT TB_DEPT_PK)*/
		DECODE(SUBSTR(MAX(dept_cd), 1, 3), null, 'DEP1000001', SUBSTR(MAX(dept_cd), 1, 3)||(SUBSTR(MAX(dept_cd), 4, 7)+1))dept_cd
	FROM TB_DEPT
	WHERE
		ROWNUM = 1)
	,#{dept_nm}
	,#{dept_num1}
	,#{dept_num2}
	,#{dept_num3}
	,#{dept_fnum1}
	,#{dept_fnum2}
	,#{dept_fnum3}
	,#{active_flg}
	,'admin'
)

UPDATE TB_DEPT
SET
	dept_nm = #{dept_nm}
	,dept_num1 = #{dept_num1}
	,dept_num2 = #{dept_num2}
	,dept_num3 = #{dept_num3}
	,dept_fnum1 = #{dept_fnum1}
	,dept_fnum2 = #{dept_fnum2}
	,dept_fnum3 = #{dept_fnum3}
	,active_flg = #{active_flg}
	,updated_by = 'admin'
	,updated = sysdate
WHERE
	dept_cd = #{dept_cd}

UPDATE TB_DEPT
SET DEL_FLG = 'Y'
WHERE DEPT_CD = #{dept_cd}
