mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rnum, m.* FROM ( SELECT DISTINCT m.menu_cd ,m.menu_nm ,m.menu_url ,m.menu_level ,( SELECT DISTINCT menu_nm FROM TB_menu WHERE m.up_menu_cd = menu_cd ) as up_menu_nm ,m.active_flg FROM TB_MENU m WHERE m.del_flg='N'
and m.menu_nm like '%'||#{menu_nm_key}||'%'
and m.active_flg=#{active_key}
ORDER BY m.menu_cd DESC ) m )
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
INSERT INTO TB_MENU (
	menu_cd
	,menu_nm
	,menu_url
	,menu_level
	,up_menu_cd
	,active_flg
	,created_by
)
VALUES (
	(SELECT /*+INDEX_DESC(TB_MENU TB_MENU_PK)*/
		DECODE(SUBSTR(MAX(menu_cd), 1, 3), null, 'MEN1000001', SUBSTR(MAX(menu_cd), 1, 3)||(SUBSTR(MAX(menu_cd), 4, 7)+1))menu_cd
	FROM TB_MENU
	WHERE
		ROWNUM = 1)
	,#{menu_nm}
	,#{menu_url}
	,#{menu_level}
	,#{up_menu_cd}
	,#{active_flg}
	,'admin'
)

UPDATE TB_MENU
SET
	menu_nm = #{menu_nm}
	,menu_url = #{menu_url}
	,menu_level = #{menu_level}
	,up_menu_cd = #{up_menu_cd}
	,active_flg = #{active_flg}
	,updated_by = 'admin'
	,updated = sysdate
WHERE
	menu_cd = #{menu_cd}

UPDATE TB_MENU
SET DEL_FLG = 'Y'
WHERE menu_cd = #{menu_cd}
