mapper.update:
UPDATE tb_user
SET DEL_FLG = 'Y'
where user_id = #{user_id}

		UPDATE
		TB_USER
		SET
		user_nm=#{user_nm}
		,user_pwd=#{user_pwd}
		,email_id=#{email_id}
		,email_domain=#{email_domain}
		,cphone_num1=#{cphone_num1}
		,cphone_num2=#{cphone_num2}
		,cphone_num3=#{cphone_num3}
		,phone_num1=#{phone_num1}
		,phone_num2=#{phone_num2}
		,phone_num3=#{phone_num3}
		,dept_cd=#{dept_cd}
		,rank_cd=#{rank_cd}
		,duty_cd=#{duty_cd}
		,updated=sysdate
		,updated_by=#{user_id}

		WHERE
		user_id = #{user_id}
